import React from 'react';
import { View, Text, Image, FlatList, TouchableOpacity, StyleSheet, ScrollView } from 'react-native';
import { NavigationContainer, NavigationProp } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { Ionicons } from '@expo/vector-icons';

const Stack = createStackNavigator();

const movieDetails = {
  title: "The Fantastic Four: First Steps",
  description:
    "Um grupo de astronautas passa por uma tempestade cósmica durante seu voo experimental. Ao retornar à Terra, os tripulantes descobrem que possuem novas e bizarras habilidades. Reed Richards pode esticar seu corpo. Sua noiva, Susan Storm, ganha a habilidade de se tornar invisível.",
  budget: 200000000,
  votes: 8500,
  duration: "150 min",
  releaseDate: "2025-06-25",
  image: "https://i0.wp.com/flixlandia.com.br/wp-content/uploads/2025/02/poster-quarteto-fantastico-e1738604111212.webp?fit=1024%2C576&ssl=1", // Pôster do filme
  cast: [
    { id: "1", name: "Mister Fantastic", actor: "Pedro Pascal", image: "data:image/jpeg;base64," },
    { id: "2", name: "Invisible Woman", actor: "Vanessa Kirb", image: "data:image/jpeg;base64," },
    { id: "3", name: "Human Torch", actor: "Joseph Quinn", image: "data:image/jpeg;base64," },
    { id: "4", name: "The Thing", actor: "Ebon Moss-Bachrach", image: "data:image/jpeg;base64," },
    { id: "5", name: "Galactus", actor: "Ralph Ineson", image: "data:image/jpeg;base64," },
  ],
};

const MovieDetailsScreen = ({ navigation }: { navigation: NavigationProp<any> }) => {
  return (
    <ScrollView style={styles.container}>

       <Image source={{ uri: movieDetails.image }} style={styles.movieImage} />

      <View style={styles.movieInfo}>
        <Text style={styles.movieTitle}>{movieDetails.title}</Text>
        <Text style={styles.movieDescription}>{movieDetails.description}</Text>
      </View>

      <View style={styles.detailsContainer}>
        <Text>Orçamento: ${movieDetails.budget.toLocaleString()}</Text>
        <Text>Votos: {movieDetails.votes}</Text>
        <Text>Duração: {movieDetails.duration}</Text>
        <Text>Lançamento: {movieDetails.releaseDate}</Text>
      </View>
      
      <Text style={styles.sectionTitle}>Atores</Text>
      <FlatList
        data={movieDetails.cast}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <TouchableOpacity style={styles.actorItem}>
            <Image source={{ uri: item.image }} style={styles.actorImage} />
            <View>
              <Text style={styles.actorName}>{item.name}</Text>
              <Text style={styles.actorCharacter}>{item.actor}</Text>
            </View>
            <Ionicons name="chevron-forward" size={20} color="gray" />
          </TouchableOpacity>
        )}
      />
    </ScrollView>
  );
};

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator screenOptions={{ headerShown: false }}>
        <Stack.Screen name="MovieDetails" component={MovieDetailsScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

